'use strict';

var child_process = require('child_process');
var path = require('path');

function runOp(command) {
  const cmd = path.join(__dirname, '../binaries/op');
  child_process.spawn(cmd, [command, ...process.argv.slice(2)], {
    stdio: ['inherit', 'inherit', 'inherit'],
    env: process.env,
    cwd: process.cwd()
  });
}

exports.runOp = runOp;
